{{ block.super }}
<script>
(function($, window, undefined){
    $(window).load(function(){
        $('#howto_sidenav').affix({
            offset: {
                top: function(){ return $('header').height() - 20 },
                bottom: function(){ return $('footer').height() + 20 }
            }
        })
    });
})(jQuery, this);
</script>

